package dh

import (
    "io"
    "errors"
    "crypto"
    "math/big"
)

// 分组 id
type GroupID uint

const (
    P1001 GroupID = 1 + iota
    P1002
    P1536
    P2048
    P3072
    P4096
    P6144
    P8192
)

var zero *big.Int = big.NewInt(0)
var one  *big.Int = big.NewInt(1)
var two  *big.Int = big.NewInt(2)

// 公用参数
type Parameters struct {
    // The prime
    P *big.Int

    // The generator
    G *big.Int
}

// 公钥
type PublicKey struct {
    Parameters

    // 公钥
    Y *big.Int
}

// 检测
// public key is < 0 or > g.P.
func (this *PublicKey) Check() (err error) {
    if !((*this.Y).Cmp(zero) >= 0 && (*this.Y).Cmp(this.P) == -1) {
        err = errors.New("peer's public is not a possible group element")
    }

    return
}

// 私钥
type PrivateKey struct {
    PublicKey

    // 私钥
    X *big.Int
}

func (this *PrivateKey) Public() crypto.PublicKey {
    return &this.PublicKey
}

// 生成证书
func GenerateKey(groupID GroupID, rand io.Reader) (*PrivateKey, *PublicKey, error) {
    param, err := GetMODPGroup(groupID)
    if err != nil {
        return nil, nil, err
    }

    if param.P == nil {
        err := errors.New("crypto/dh: prime is nil")
        return nil, nil, err
    }
    if param.G == nil {
        err := errors.New("crypto/dh: generator is nil")
        return nil, nil, err
    }

    min := big.NewInt(int64(param.P.BitLen() + 1))
    bytes := make([]byte, (param.P.BitLen()+7)/8)

    private := &PrivateKey{}
    private.PublicKey.Parameters = Parameters{
        P: param.P,
        G: param.G,
    }

    for private.X == nil {
        _, err := io.ReadFull(rand, bytes)
        if err != nil {
            private.X = nil
            return nil, nil, errors.New("private x is nil")
        }

        // Clear bits in the first byte to increase
        // the probability that the candidate is < g.P.
        bytes[0] = 0
        if private.X == nil {
            private.X = new(big.Int)
        }

        (*private.X).SetBytes(bytes)
        if (*private.X).Cmp(min) < 0 {
            private.X = nil
        }
    }

    private.PublicKey.Y = new(big.Int).Exp(param.G, private.X, param.P)

    public := &private.PublicKey

    return private, public, nil
}

// 生成密钥
func ComputeSecret(private *PrivateKey, peersPublic *PublicKey) *big.Int {
    secret := new(big.Int).Exp(peersPublic.Y, private.X, private.P)

    return secret
}

func IsSafePrimeGroup(param Group, n int) bool {
    q := new(big.Int).Sub(param.P, one)
    q = q.Div(q, two)

    return q.ProbablyPrime(n)
}

// 分组数据
type Group struct {
    // The prime
    P *big.Int

    // The generator
    G *big.Int
}

// Current minimum recommendation is 2048 bit with id=14.
func GetMODPGroup(groupID GroupID) (group *Group, err error) {
    switch groupID {
        case P1001:
            p, _ := new(big.Int).SetString("FFFFFFFFFFFFFFFFC90FDAA22168C234C4C6628B80DC1CD129024E088A67CC74020BBEA63B139B22514A08798E3404DDEF9519B3CD3A431B302B0A6DF25F14374FE1356D6D51C245E485B576625E7EC6F44C42E9A63A3620FFFFFFFFFFFFFFFF", 16)
            group = &Group{
                G: big.NewInt(2),
                P: p,
            }
        case P1002:
            p, _ := new(big.Int).SetString("FFFFFFFFFFFFFFFFC90FDAA22168C234C4C6628B80DC1CD129024E088A67CC74020BBEA63B139B22514A08798E3404DDEF9519B3CD3A431B302B0A6DF25F14374FE1356D6D51C245E485B576625E7EC6F44C42E9A637ED6B0BFF5CB6F406B7EDEE386BFB5A899FA5AE9F24117C4B1FE649286651ECE65381FFFFFFFFFFFFFFFF", 16)
            group = &Group{
                G: big.NewInt(2),
                P: p,
            }
        // 1536-bit
        case P1536:
            p, _ := new(big.Int).SetString("", 16)
            group = &Group{
                G: big.NewInt(2),
                P: p,
            }
        // 2048-bit
        case P2048:
            p, _ := new(big.Int).SetString("", 16)
            group = &Group{
                G: big.NewInt(2),
                P: p,
            }
        // 3072-bit
        case P3072:
            p, _ := new(big.Int).SetString("", 16)
            group = &Group{
                G: big.NewInt(2),
                P: p,
            }
        // 4096-bit
        case P4096:
            p, _ := new(big.Int).SetString("", 16)
            group = &Group{
                G: big.NewInt(2),
                P: p,
            }
        // 6144-bit
        case P6144:
            p, _ := new(big.Int).SetString("", 16)
            group = &Group{
                G: big.NewInt(2),
                P: p,
            }
        // 8192-bit
        case P8192:
            p, _ := new(big.Int).SetString("", 16)
            group = &Group{
                G: big.NewInt(2),
                P: p,
            }
        default:
            group = nil
            err = errors.New("DH: Unknown group")
    }

    return
}
