type testData struct {
    msg []byte
    md []byte
}

func Test_Hash224_Check(t *testing.T) {
   tests := []testData{
        {
           fromHex("1F877C"),
           fromHex("c8b22035a5357c9b31cd13788b7e898ad61d87fb459e5e309def9e9b"),
        },
        {
           fromHex("FDFDFDFDFDFDFDFDFDFDFDFDFDFDFDFD"),
           fromHex("469ad4041d96be92aad4c56b1d5172fb292aff43dd51ff800a6bf97f"),
        },
        {
           fromHex(""),
           fromHex("71fa222fdbc3d40bf63c3d81dfb0324a850138ae61ec3f94cb5ca3ba"),
        },
        {
           fromHex(""),
           fromHex("d7f68731763d919c084fd782298a40f7e2a8d898e61cd05b3a0c8716"),
        },
        {
           fromHex(""),
           fromHex("63ef25e18d4fa7fbfa88322906148cb2b0aa21250844b9650794a6f3"),
        },
    }

    h := New224()

    for i, test := range tests {
        h.Reset()
        h.Write(test.msg)
        sum := h.Sum(nil)

        if !bytes.Equal(sum, test.md) {
            t.Errorf("[%d] New224 fail, got %x, want %x", i, sum, test.md)
        }

        // =====

        sum2 := Sum224(test.msg)

        if !bytes.Equal(sum2[:], test.md) {
            t.Errorf("[%d] Sum224 fail, got %x, want %x", i, sum2, test.md)
        }
    }
}
