package esch

import (
    "fmt"
    "testing"

    "github.com/deatil/go-hash/hash"
)

func Test_Name384(t *testing.T) {
    if ESCH384.String() != "ESCH384" {
        t.Error("ESCH384 name fail")
    }
}

var test384Datas = []struct {
    output string
    input  string
}{
    {"900c76a75ad5fec6924934e8eadc78bcb3951e241a2ac9301e6d35895689ba7c93411a5b6def5a2f87248aff1bdd240e", "00010203"},
    {"dcad7d7394c64cb59be79ee06a42fe5a420c5718156c6d3cc44ed07e699ddbe79bb2919d65ec4a24b5ece4afb11dff54", "000102030405060708090A0B0C0D0E0F10111213141516"},
    {"9a3a811a27053e3cd4d7f09fa6190c64ff2014d311bcd2f70390da38a99980b35f07f1dd9d93434d8a98552069d8e228", ""},
}

func Test_SumBy384(t *testing.T) {
    for index, test := range test384Datas {
        e := hash.
            FromBytes(fromHex(test.input)).
            SumBy(ESCH384)

        t.Run(fmt.Sprintf("SumBy %d", index), func(t *testing.T) {
            if e.Error != nil {
                t.Fatal(e.Error)
            }

            if e.ToHexString() != test.output {
                t.Errorf("got %s, want %s", e.ToHexString(), test.output)
            }
        })
    }
}

func Test_NewBy384(t *testing.T) {
    for index, test := range test384Datas {
        e := hash.
            Hashing().
            NewBy(ESCH384).
            Write(fromHex(test.input)).
            Sum(nil)

        t.Run(fmt.Sprintf("NewBy %d", index), func(t *testing.T) {
            if e.Error != nil {
                t.Fatal(e.Error)
            }

            if e.ToHexString() != test.output {
                t.Errorf("got %s, want %s", e.ToHexString(), test.output)
            }
        })
    }
}
